local tbTable = GameMain:GetMod("MapStoryHelper");
local cRegion = story.target.region;
local unionData = cRegion.UnionData;
local solve = cRegion.RegionPolicy.Way;
local enumSolve = CS.XiaWorld.OutspreadMgr.Region.SolveWay;
tbTable:DefuseSubmiss(WorldLua, cRegion);
if not solve or solve == enumSolve.None then
	tbTable:PrintSolution(WorldLua, "None");
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Social then
	tbTable:PrintSolution(WorldLua, "None");
	WorldLua:AddMsg(XT("代理人想了想，依靠处世无法密谋。"));
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Fight then
	tbTable:PrintSolution(WorldLua, "Fight");
	WorldLua:AddMsg(XT("代理人决定派人去请匪徒来此地扰乱民生。晚上，一群匪徒袭击了这里。"));
	tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
elseif solve == enumSolve.Charm then
	tbTable:PrintSolution(WorldLua, "Charm");
	WorldLua:AddMsg(XT("代理人决定直接拉拢其他门派的信众。"));
	local a = cRegion:GetNotSelfRegionSchool();
	if a > 0 then
		tbTable:DirectlyGrabOtherPop(WorldLua, cRegion, a);
	else
		WorldLua:AddMsg(XT("此地并无其他势力。"));
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Intelligence then
	tbTable:PrintSolution(WorldLua, "Intelligence");
	WorldLua:AddMsg(XT("代理人决定去做些坏事，降低其他势力的吸引力。"));
	local a = cRegion:GetNotSelfRegionSchool();
	if a > 0 then
		tbTable:DecreaseOtherAttra(WorldLua, cRegion, a);
	else
		WorldLua:AddMsg(XT("此地并无其他势力。"));
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Food then
	tbTable:PrintSolution(WorldLua, "Food");
	WorldLua:AddMsg(XT("代理人拿了一碗面。大家不知道他在干什么。"));
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.LingStone then
	tbTable:PrintSolution(WorldLua, "LingStone");
	WorldLua:AddMsg(XT("代理人拿了一颗灵石，大家不知道他在干什么。"));
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Wood then
	tbTable:PrintSolution(WorldLua, "Wood");
	WorldLua:AddMsg(XT("代理人拿了一根棍子，大家不知道他在干什么。"));
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Rock then
	tbTable:PrintSolution(WorldLua, "Rock");
	WorldLua:AddMsg(XT("代理人拿了一块石头，大家不知道他在干什么。"));
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Member then
	tbTable:PrintSolution(WorldLua, "Member");
	WorldLua:AddMsg(XT("代理人看了一眼众弟子，好像没有谁比较特别，微微叹了口气。"));
	tbTable:NoEvent(WorldLua, cRegion);
end

local tbTable = GameMain:GetMod("MapStoryHelper");
local cRegion = story.target.region;
local School1 = story.target.schools[0];
local unionData = cRegion.UnionData;
local solve = cRegion.RegionPolicy.Way;
local enumSolve = CS.XiaWorld.OutspreadMgr.Region.SolveWay;
tbTable:DefuseSubmiss(WorldLua, cRegion);
if not solve or solve == enumSolve.None then
	tbTable:PrintSolution(WorldLua, "None");
	WorldLua:AddMsg(XT("布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Social then
	tbTable:PrintSolution(WorldLua, "None");
	WorldLua:AddMsg(XT("代理人走上前去，说道：“依我看这世道，要不还是算了吧。”对方有点恼火，说：“你是哪个派的？管得着吗？”代理人无奈只能离去。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Fight then
	tbTable:PrintSolution(WorldLua, "Fight");
	WorldLua:AddMsg(XT("代理人直接走上去，与对方打了起来，但被信众阻止了。群众用异样的眼神看着代理人。布施中断了一会又顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
elseif solve == enumSolve.Charm then
	tbTable:PrintSolution(WorldLua, "Charm");
	WorldLua:AddMsg(XT("代理人当着他们的面，开始大讲道法，想要吸引信众。但群众此时更需要的是布施的食物。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Intelligence then
	tbTable:PrintSolution(WorldLua, "Intelligence");
	if unionData.Intelligence > tbTable:MPL(1) then
		WorldLua:AddMsg(XT("代理人决定偷偷将其布施的食物替换成腐坏的食物。群众吃了之后，当场觉得不对劲。布施中断了。"));
		tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
		tbTable:DecreaseOtherAttra(WorldLua, cRegion, School1);
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("代理人决定偷偷将其布施的食物替换成腐坏的食物。但是由于不够机灵，被抓了个现行。代理人被一脚踹到了树上。布施顺利进行。"));
		tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
		tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
	end
elseif solve == enumSolve.Food then
	tbTable:PrintSolution(WorldLua, "Food");
	WorldLua:AddMsg(XT("代理人拿了一些食物，也想要布施。但奈何没有充足的准备，无法与他们抗衡。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.LingStone then
	tbTable:PrintSolution(WorldLua, "LingStone");
	if CS.XiaWorld.OutspreadMgr.Instance:CanCostItem("Item_LingStone", 100) then
		WorldLua:AddMsg(XT("代理人与对方私下沟通，小声说道：“今天要么就先这样吧，下次再布施怎么样？”并将灵石塞到他们代理人手中。“看什么看？走了走了，今天到此为止。”对方代理人当即决定回家。布施中断了。"));
		CS.XiaWorld.OutspreadMgr.Instance:CostItem(cRegion, "Item_LingStone", 100, nil);
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
		WorldLua:AddMsg(XT("灵石-100"));
	else
		WorldLua:AddMsg(XT("代理人摸了摸口袋，并与门派沟通，发现连100灵石都没有。"));
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Wood then
	tbTable:PrintSolution(WorldLua, "Wood");
	WorldLua:AddMsg(XT("代理人拿了一根棍子，准备直接攻击对方代理人，然而他们信众众多，及时阻止了。群众看见了之后对我们表示不满，并心有不安。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
	tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
elseif solve == enumSolve.Rock then
	tbTable:PrintSolution(WorldLua, "Rock");
	WorldLua:AddMsg(XT("代理人拿了一块石头向对方砸了过去，但被其接住。“你们看看，这就是他们的作风！”群众对我们表示怀疑，并心有不安。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
	tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
elseif solve == enumSolve.Member then
	tbTable:PrintSolution(WorldLua, "Member");
	WorldLua:AddMsg(XT("代理人看了一眼众弟子，好像没有谁比较特别，微微叹了口气。布施顺利进行。"));
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
end

local tbTable = GameMain:GetMod("MapStoryHelper");
local cRegion = story.target.region;
local School1 = story.target.schools[0];
local unionData = cRegion.UnionData;
local solve = cRegion.RegionPolicy.Way;
local enumSolve = CS.XiaWorld.OutspreadMgr.Region.SolveWay;
tbTable:DefuseSubmiss(WorldLua, cRegion);
if not solve or solve == enumSolve.None then
	tbTable:PrintSolution(WorldLua, "None");
	WorldLua:AddMsg(XT("传道顺利进行。"));
	tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Social then
	tbTable:PrintSolution(WorldLua, "None");
	WorldLua:AddMsg(XT("代理人走上前去，说道：“依我看这世道，要不还是算了吧。”对方有点恼火，说：“你是哪个派的？管得着吗？”代理人无奈只能离去。传道顺利进行。"));
	tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Fight then
	tbTable:PrintSolution(WorldLua, "Fight");
	if unionData.Fight > tbTable:SPL(1) then
		WorldLua:AddMsg(XT("代理人直接走上去，大喊：“你们讲得毫无道理。简直像是在放屁！”他们派出了一名弟子与代理人决斗。代理人身强体壮，将其制服。对方颜面扫地。传道中断了，人们看了决斗也觉得心有不安。"));
		tbTable:DecreaseOtherAttra(WorldLua, cRegion, School1);
		tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
	else
		WorldLua:AddMsg(XT("代理人直接走上去，大喊：“你们讲得毫无道理。简直像是在放屁！”他们派出了一名弟子与代理人决斗。代理人竟被其一掌击退到了路中间，只能灰溜溜地离开，然而他们的传道也不尽如意。"));
		tbTable:SlightlyIncreaseOtherAttra(WorldLua, cRegion, School1);
		tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
	end
elseif solve == enumSolve.Charm then
	tbTable:PrintSolution(WorldLua, "Charm");
	if unionData.Charm > tbTable:SPL(1) then
		WorldLua:AddMsg(XT("代理人走上前去，同样也开始传道。他展现了十足的魅力，群众纷纷决定成为我们的信徒。"));
		tbTable:DirectlyGrabOtherPopSlightly(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("代理人走上前去，同样也开始传道。但可惜魅力不足，群众看都不看他一眼。传道顺利进行。"));
		tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
	end
elseif solve == enumSolve.Intelligence then
	tbTable:PrintSolution(WorldLua, "Intelligence");
	if unionData.Intelligence > tbTable:MPL(1) then
		WorldLua:AddMsg(XT("代理人走上前去，决定与他们辩论一番。相互之间说了很多群众根本无法听懂的话，争得面红耳赤。争论结果已经不重要，群众早已觉得厌烦并离开。传道中断了。"));
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
		tbTable:SlightlyDecreaseOtherAttra(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("代理人走上前去，决定与他们辩论一番。然而由于代理人知识尚浅，三言两语就被驳了回来。传道顺利进行。"));
		tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
	end
elseif solve == enumSolve.Food then
	tbTable:PrintSolution(WorldLua, "Food");
	WorldLua:AddMsg(XT("代理人拿了一碗面。大家不知道他在干什么。传道顺利进行。"));
	tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.LingStone then
	tbTable:PrintSolution(WorldLua, "LingStone");
	if CS.XiaWorld.OutspreadMgr.Instance:CanCostItem("Item_LingStone", 100) then
		WorldLua:AddMsg(XT("代理人与对方私下沟通，小声说道：“今天要么就先这样吧，下次再传教怎么样？”并将灵石塞到他们代理人手中。“啊，今天我身体不适，改日再讲。”对方代理人当即决定回家。传道中断了。"));
		CS.XiaWorld.OutspreadMgr.Instance:CostItem(cRegion, "Item_LingStone", 100, nil);
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("代理人摸了摸口袋，并与门派沟通，发现连100灵石都没有。"));
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Wood then
	tbTable:PrintSolution(WorldLua, "Wood");
	WorldLua:AddMsg(XT("代理人拿了一根棍子，准备直接攻击对方代理人，然而他们信众众多，及时阻止了。群众看见了之后对我们表示不满，并心有不安。传道顺利进行。"));
	tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
	tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
elseif solve == enumSolve.Rock then
	tbTable:PrintSolution(WorldLua, "Rock");
	WorldLua:AddMsg(XT("代理人拿了一块石头向对方砸了过去，但被其接住。“你们看看，这就是他们的作风！”群众对我们表示怀疑，并心有不安。传道顺利进行。"));
	tbTable:IncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
	tbTable:SlightlyDecreaseStab(WorldLua, cRegion);
elseif solve == enumSolve.Member then
